mapper.select:
select

from account
where status="0"

	and manager_up_id =#{managerUpId,jdbcType=INTEGER}

order by id ASC limit
#{startNum,jdbcType=INTEGER},#{pageNumber,jdbcType=INTEGER}

mapper.update:
